  money, permission, remark
  )
values (#{id,jdbcType=INTEGER}, #{userid,jdbcType=INTEGER}, #{gradeid,jdbcType=INTEGER}, 
  #{courseid,jdbcType=INTEGER}, #{period,jdbcType=VARCHAR}, #{date,jdbcType=DATE}, 
  #{money,jdbcType=DOUBLE}, #{permission,jdbcType=INTEGER}, #{remark,jdbcType=VARCHAR}
  )

mapper.select:

select id, userId, gradeId, courseId, period, date, money, permission, remark
from info
where id = #{id,jdbcType=INTEGER}

select id, userId, gradeId, courseId, period, date, money, permission, remark
from info
where userId = #{id,jdbcType=INTEGER}

